import Slider from "react-slick";
import React, { useState } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Carousel.css";

import img_1 from "./foto/1.png";
import img_2 from "./foto/2.png";
import img_3 from "./foto/3.png";
import img_4 from "./foto/4.png";
import img_5 from "./foto/5.png";
import img_6 from "./foto/6.png";

export const Carousel = () => {
  const [mainImage, setMainImage] = useState([
    {
      id: 1,
      src: img_1,
    },
  ]);
  const [smallImages, setSmallImages] = useState([
    { id: 2, src: img_2 },
    { id: 3, src: img_3 },
    { id: 4, src: img_4 },
    { id: 5, src: img_5 },
    { id: 6, src: img_6 },
  ]);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <div>
      <h2> Single Item</h2>
      <Slider {...settings}>
        <div className="slider__content">
          <div className="big__image">
            <img src={mainImage.src} alt="" />
          </div>
        </div>
        <div>
          <h3>2</h3>
        </div>
        <div>
          <h3>3</h3>
        </div>
        <div>
          <h3>4</h3>
        </div>
        <div>
          <h3>5</h3>
        </div>
        <div>
          <h3>6</h3>
        </div>
      </Slider>
    </div>
  );
};
