import { useState } from "react";
import HeaderForm from "./Header-form";

function HeaderMenu() {
  const [showMode, setShowMode] = useState(false);

  const handlerClick = () => {
    setShowMode(!showMode);
  };
  let content = null;

  if (showMode) {
    content = (
      <>
        <HeaderForm />
      </>
    );
  }
  let submenu = null;
  const handlerHover = () => {
    submenu = (
      <div className="submenu">
        <ul className="submenu__items">
          <li className="submenu__item">
            <a href="" className="submenu__link">
              О нас
            </a>
          </li>
          <li className="submenu__item">
            <a href="" className="submenu__link">
              Первая стрижка
            </a>
          </li>
          <li className="submenu__item">
            <a href="" className="submenu__link">
              Прокалывание мочек ушей
            </a>
          </li>
          <li className="submenu__item">
            <a href="" className="submenu__link">
              Отзывы
            </a>
          </li>
          <li className="submenu__item">
            <a href="" className="submenu__link">
              Вакансии
            </a>
          </li>
        </ul>
      </div>
    );
  };

  return (
    <ul className="menu__items">
      <li className="menu__item">
        <a onMouseEnter={handlerHover} href="" className="menu__link">
          Студия
        </a>
        {submenu}
      </li>
      <li className="menu__item">
        <a href="" className="menu__link">
          Услуги{" "}
        </a>
      </li>
      <li className="menu__item">
        <a href="" className="menu__link">
          Акции
        </a>
      </li>
      <li className="menu__item">
        <a href="" className="menu__link">
          Каталог причесок
        </a>
      </li>
      <button className="btn" onClick={handlerClick}>
        {" "}
        Записаться
      </button>
      {content}
    </ul>
  );
}

export default HeaderMenu;
