import "./Header.css";
import logo from "../../images/img/header-logo.svg";
import HeaderMenu from "./Header-menu";

function Header() {
  const array = [
    { name: "Студия", link: "/" },
    { name: "Услуги", link: "/services" },
    { name: "Акции", link: "/" },
    { name: "Каталог причесок", link: "/catalog" },
  ];

  const [showMode, setShowMode] = useState(false);
  const handlerClick = () => {
    setShowMode(!showMode);
  };

  let content = null;

  if (showMode) {
    content = (
      <>
        <HeaderForm />
      </>
    );
  }

  return (
    <section className="header">
      <div className="header__box">
        <a href="#">
          <img src={logo} alt="" />
        </a>
        <nav className="menu">
          <ul className="menu__items">
            {array.map((item) => {
              <HeaderMenu name={item.name} link={item.link} />;
            })}
          </ul>
          <button className="btn" onClick={handlerClick}>
            Записаться
          </button>
          {content}
        </nav>
      </div>
    </section>
  );
}

export default Header;
